import React, { useState } from 'react';
import MobileNavToggle from './MobileNavToggle';

function Header() {
    const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);
    // Function to handle the mobile nav toggle
  const handleMobileNavToggle = () => {
    // Toggle the isMobileNavOpen state
    setIsMobileNavOpen(!isMobileNavOpen);
  };
  return (
      <div>
          
        {/* Pass the handleMobileNavToggle function to MobileNavToggle */}
          <MobileNavToggle onClick={handleMobileNavToggle} />
            {/* ======= Header ======= */}
      <header
        id="header"
        className={`d-flex flex-column justify-content-center ${isMobileNavOpen ? 'open' : ''}`}
      >
        <nav
          id="navbar"
          className={`navbar nav-menu ${isMobileNavOpen ? 'open' : ''}`}
        >
          <ul>
            <li><a href="#hero" className="nav-link scrollto active"><i className="bx bx-home"></i> <span>Home</span></a></li>
            <li><a href="#mipresento" className="nav-link scrollto"><i className="bx bx-user"></i> <span>Mi presento</span></a></li>
            <li><a href="#portfolio" className="nav-link scrollto"><i className="bx bx-book-content"></i> <span>Portfolio</span></a></li>
            <li><a href="#services" className="nav-link scrollto"><i className="bx bx-server"></i> <span>Servizi</span></a></li>
            <li><a href="#contact" className="nav-link scrollto"><i className="bx bx-envelope"></i> <span>Contatti</span></a></li>
            <li><a href="blog.html" className="nav-link scrollto"><i className="bi bi-journal"></i> <span>Blog</span></a></li>
            <li><a href="prenota-call.html" className="nav-link scrollto"><i className="bi bi-calendar-event"></i> <span>Appuntamenti</span></a></li>
          </ul>
        </nav>
      </header>
    </div>
  );
}

export default Header;
