import React, { useState } from "react";

function Contact() {
  const [alertVisible, setAlertVisible] = useState(false);
  const [formValues, setFormValues] = useState({ nome: "", email: "", subject: "", messaggio: "" });

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData(event.target);

    try {
      const response = await fetch("http://localhost:5050/contatti", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        // La richiesta al server Ã¨ andata a buon fine, mostra l'alert
        setAlertVisible(true);
        setFormValues({ nome: "", email: "", subject: "", messaggio: "" }); // Resetta il modulo
      } else {
        // La richiesta al server ha generato un errore
        console.error("Errore durante l'invio del modulo:", response.status, response.statusText);
        // Puoi anche mostrare un messaggio di errore qui se lo desideri
      }
    } catch (error) {
      console.error("Errore durante l'invio del modulo:", error);
      // Puoi anche gestire gli errori qui se lo desideri
    }
  };

  return (
    <section id="contact" className="contact">
      <div className="container" data-aos="fade-up">
        <div className="section-title">
          <h2>Contatti</h2>
        </div>

        <div className="row mt-1">
          <div className="col-lg-4">
            <div className="info">
              <div className="address">
                <i className="bi bi-geo-alt"></i>
                <h4>Lavoro a:</h4>
                <p>Ferrara, Bologna, Comacchio, Ravenna e Rovigo.</p>
              </div>
              <div className="email">
                <i className="bi bi-envelope"></i>
                <h4>Email:</h4>
                <p>elena@artisticamenteonline.it</p>
              </div>
              <div className="phone">
                <i className="bi bi-phone"></i>
                <h4>Cell:</h4>
                <p>3519368237</p>
              </div>
            </div>
          </div>

          <div className="col-lg-8 mt-5 mt-lg-0">
            <form onSubmit={handleSubmit} className="php-email-form">
              <div className="row">
                <div className="col-md-6 form-group">
                  <input
                    type="text"
                    name="nome"
                    className="form-control"
                    id="nome"
                    placeholder="Nome"
                    required
                    value={formValues.nome}
                    onChange={(e) => setFormValues({ ...formValues, nome: e.target.value })}
                  />
                </div>
                <div className="col-md-6 form-group mt-3 mt-md-0">
                  <input
                    type="email"
                    className="form-control"
                    name="email"
                    id="email"
                    placeholder="Email"
                    required
                    value={formValues.email}
                    onChange={(e) => setFormValues({ ...formValues, email: e.target.value })}
                  />
                </div>
              </div>
              <div className="form-group mt-3">
                <input
                  type="text"
                  className="form-control"
                  name="subject"
                  id="subject"
                  placeholder="Oggetto"
                  required
                  value={formValues.subject}
                  onChange={(e) => setFormValues({ ...formValues, subject: e.target.value })}
                />
              </div>
              <div className="form-group mt-3">
                <textarea
                  className="form-control"
                  name="messaggio"
                  id="messaggio"
                  rows="5"
                  placeholder="Messaggio"
                  required
                  value={formValues.messaggio}
                  onChange={(e) => setFormValues({ ...formValues, messaggio: e.target.value })}
                ></textarea>
              </div>
              <div className="my-3">
                <div className={`alert ${alertVisible ? 'alert-visible' : ''}`} id="alert">
                  <p>Mail inviata!</p>
                  <button
                    id="ok-button"
                    onClick={() => {
                      setAlertVisible(false);
                      setFormValues({ nome: "", email: "", subject: "", messaggio: "" }); // Resetta il modulo
                    }}
                  >
                    OK
                  </button>
                </div>
              </div>
              <div className="text-center">
                <button type="submit">Invia messaggio</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
