import React, { useState } from 'react';

const Portfolio = () => {
  const [lightboxIsOpen, setLightboxIsOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);
  const portfolioImages = [
    { src: 'assets/img/portfolio/portfolio-1.jpg', title: 'App 1' },
    // Aggiungi ulteriori immagini con i loro URL e titoli
  ];

  const openLightbox = (imageIndex) => {
    setSelectedImageIndex(imageIndex);
    setLightboxIsOpen(true);
  };

  const closeLightbox = () => {
    setLightboxIsOpen(false);
  };

  return (
    <section id="portfolio" className="portfolio section-bg">
      <div className={`lightbox-overlay ${lightboxIsOpen ? 'active' : ''}`}>
        <div className="custom-lightbox">
          <span className="custom-lightbox-close" onClick={closeLightbox}>
            <i className="bx bx-x" />
          </span>
          <img
            src={portfolioImages[selectedImageIndex].src}
            alt={portfolioImages[selectedImageIndex].title}
          />
          <p>{portfolioImages[selectedImageIndex].title}</p>
        </div>
      </div>

      <div className="container" data-aos="fade-up">
        <div className="row portfolio-container" data-aos="fade-up" data-aos-delay="200">
          {portfolioImages.map((image, index) => (
            <div className="col-lg-4 col-md-6 portfolio-item filter-app" key={index}>
              <div className="portfolio-wrap">
                <img
                  src={image.src}
                  className="img-fluid"
                  alt=""
                  onClick={() => openLightbox(index)}
                />
                <div className="portfolio-info">
                  <h4>{image.title}</h4>
                  <p>App</p>
                  <a onClick={() => openLightbox(index)} className="portfolio-lightbox" title={`App ${index + 1}`}>
                    <i className="bx bx-plus"></i>
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
