import React, { useState } from 'react';
import Lightbox from 'yet-another-react-lightbox';

function Portfolio() {
  const [isOpen, setIsOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  // Definisci i dati del tuo portfolio
  const portfolioItems = [
    {
      title: 'App 1',
      category: 'App',
      image: 'assets/img/portfolio/portfolio-1.jpg',
    },
    {
      title: 'Web 3',
      category: 'Web',
      image: 'assets/img/portfolio/portfolio-2.jpg',
    },
    {
      title: 'App 2',
      category: 'App',
      image: 'assets/img/portfolio/portfolio-3.jpg',
    },
    {
      title: 'Card 2',
      category: 'Card',
      image: 'assets/img/portfolio/portfolio-4.jpg',
    },
    {
      title: 'Web 2',
      category: 'Web',
      image: 'assets/img/portfolio/portfolio-5.jpg',
    },
    {
      title: 'App 3',
      category: 'App',
      image: 'assets/img/portfolio/portfolio-6.jpg',
    },
    {
      title: 'Card 1',
      category: 'Card',
      image: 'assets/img/portfolio/portfolio-7.jpg',
    },
    {
      title: 'Card 3',
      category: 'Card',
      image: 'assets/img/portfolio/portfolio-8.jpg',
    },
    {
      title: 'Web 3',
      category: 'Web',
      image: 'assets/img/portfolio/portfolio-9.jpg',
    },
  ];

  return (
    <div>
      {portfolioItems.map((item, index) => (
        <div key={index} className="portfolio-item">
          <img
            src={item.image}
            alt={item.title}
            onClick={() => {
              setIsOpen(true);
              setPhotoIndex(index);
            }}
          />
          <h4>{item.title}</h4>
          <p>{item.category}</p>
        </div>
      ))}

      <Lightbox
        images={portfolioItems.map((item) => item.image)}
        isOpen={isOpen}
        onClose={() => setIsOpen(false)}
        currentIndex={photoIndex}
      />
    </div>
  );
}

export default Portfolio;
