import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import Head from './components/Head';
import Header from './components/Header';
import Hero from './components/Hero';
import MainIndex from './components/MainIndex';
import Footer from './components/Footer';
import Freccia from './components/Freccia';
import Script from './components/Script';
import Preloader from './components/Preloader';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simula un caricamento asincrono (puoi sostituire questo con la tua logica effettiva)
    const fakeAsyncLoad = async () => {
      try {
        // Simula un ritardo di 2 secondi prima di considerare il caricamento completato
        await new Promise((resolve) => setTimeout(resolve, 2000));
        setIsLoading(false); // Imposta lo stato su "non in caricamento" dopo il ritardo
      } catch (error) {
        console.error('Errore nel caricamento:', error);
        setIsLoading(false); // Assicurati che "isLoading" venga comunque impostato su false in caso di errore
      }
    };

    fakeAsyncLoad();
  }, []); // L'hook useEffect viene eseguito solo una volta all'avvio dell'app

  return (
    <Router>
      <div>
        <Head />
        <Header />
        <Hero />
        <MainIndex />
        <Footer />
        <Freccia />
        <Script />

        {/* Mostra il Preloader solo durante il caricamento iniziale */}
        {isLoading ? <Preloader /> : null}

        {/* Altri componenti e routing delle pagine */}
      </div>
    </Router>
  );
}

export default App;
