import React, { useState } from 'react';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';

const Portfolio = () => {
  const [lightboxIsOpen, setLightboxIsOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);

  const portfolioImages = [
    { src: 'assets/img/portfolio/portfolio-1.jpg', title: 'App 1' },
    // Aggiungi ulteriori immagini con i loro URL e titoli
  ];

  const openLightbox = (imageIndex) => {
    setSelectedImageIndex(imageIndex);
    setLightboxIsOpen(true);
  };

  return (
    <section id="portfolio" className="portfolio section-bg">
      <div className="container" data-aos="fade-up">
        <div className="section-title">
          <h2>Portfolio</h2>
          <p>Voglio darti tutte le informazioni che ti possano essere utili
            alla valutazione di una nostra collaborazione. Ecco alcuni dei miei progetti. Spero di essere la persona che stavi cercando per il tuo progetto.</p>
        </div>

        <div className="row">
          <div className="col-lg-12 d-flex justify-content-center" data-aos="fade-up" data-aos-delay="100">
            <ul id="portfolio-flters">
              <li data-filter="*" className="filter-active">Tutto</li>
              <li data-filter=".filter-app">Web</li>
              <li data-filter=".filter-card">Grafica</li>
              <li data-filter=".filter-web">Marketing</li>
            </ul>
          </div>
        </div>

        <div className="row portfolio-container" data-aos="fade-up" data-aos-delay="200">
          {portfolioImages.map((image, index) => (
            <div className="col-lg-4 col-md-6 portfolio-item filter-app" key={index}>
              <div className="portfolio-wrap">
                <img src={image.src} className="img-fluid" alt="" />
                <div className="portfolio-info">
                  <h4>{image.title}</h4>
                  <p>App</p>
                  <div className="portfolio-links">
                    <button onClick={() => openLightbox(index)} className="portfolio-lightbox" title={`App ${index + 1}`}>
                      <i className="bx bx-plus"></i>
                    </button>
                    <a href="portfolio-details.html" className="portfolio-details-lightbox" data-glightbox="type: external" title="Portfolio Details">
                      <i className="bx bx-link"></i>
                    </a>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      {lightboxIsOpen && (
        <Lightbox
          mainSrc={portfolioImages[selectedImageIndex].src}
          nextSrc={portfolioImages[(selectedImageIndex + 1) % portfolioImages.length].src}
          prevSrc={portfolioImages[(selectedImageIndex + portfolioImages.length - 1) % portfolioImages.length].src}
          onCloseRequest={() => setLightboxIsOpen(false)}
          onMovePrevRequest={() =>
            setSelectedImageIndex((selectedImageIndex + portfolioImages.length - 1) % portfolioImages.length)
          }
          onMoveNextRequest={() =>
            setSelectedImageIndex((selectedImageIndex + 1) % portfolioImages.length)
          }
        />
      )}
    </section>
  );
};

export default Portfolio;
