import React, { useState } from 'react';

const Portfolio = () => {
  const [isLightboxOpen, setIsLightboxOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const portfolioImages = [
    { src: 'assets/img/portfolio/portfolio-1.jpg', title: 'App 1' },
    // Aggiungi ulteriori immagini con i loro URL e titoli
  ];

  const openLightbox = (image) => {
    setSelectedImage(image);
    setIsLightboxOpen(true);
  };

  const closeLightbox = () => {
    setSelectedImage(null);
    setIsLightboxOpen(false);
  };

  return (
    <section id="portfolio" className="portfolio section-bg">
      <div className={`lightbox-overlay ${isLightboxOpen ? 'active' : ''}`} onClick={closeLightbox}>
        <div className="lightbox-container">
          {selectedImage && (
            <div className="lightbox-content">
              <span className="lightbox-close" onClick={closeLightbox}>&times;</span>
              <img src={selectedImage.src} alt={selectedImage.title} />
              <p>{selectedImage.title}</p>
            </div>
          )}
        </div>
      </div>

      <div className="container" data-aos="fade-up">
        <div className="row portfolio-container" data-aos="fade-up" data-aos-delay="200">
          {portfolioImages.map((image, index) => (
            <div className="col-lg-4 col-md-6 portfolio-item filter-app" key={index}>
              <div className="portfolio-wrap">
                <img
                  src={image.src}
                  className="img-fluid"
                  alt=""
                  onClick={() => openLightbox(image)}
                />
                <div className="portfolio-info">
                  <h4>{image.title}</h4>
                  <p>App</p>
                  <a onClick={() => openLightbox(image)} className="portfolio-lightbox" title={`App ${index + 1}`}>
                    <i className="bx bx-plus"></i>
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
