import React, { useState } from 'react';
import Lightbox from 'yet-another-react-lightbox'; // Importa il componente Lightbox da YARL

const Portfolio = () => {
  // Stato per gestire l'apertura del lightbox
  const [lightboxIsOpen, setLightboxIsOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);

  // Esempio di dati delle immagini del portfolio
  const portfolioImages = [
    { src: 'assets/img/portfolio/portfolio-1.jpg', title: 'App 1' },
    { src: 'assets/img/portfolio/portfolio-2.jpg', title: 'Web 3' },
    // Aggiungi ulteriori oggetti per le tue immagini
  ];

  // Funzione per aprire il lightbox con l'immagine selezionata
  const openLightbox = (imageIndex) => {
    setSelectedImageIndex(imageIndex);
    setLightboxIsOpen(true);
  };

  return (
    <section id="portfolio" className="portfolio section-bg">
      <div className="container" data-aos="fade-up">
        {/* Altri elementi del portfolio */}
         <div class="section-title">
          <h2>Portfolio</h2>
          <p>Voglio darti tutte le informazioni che ti possano essere utili 
             alla valutazione di una nostra collaborazione.Ecco alcuni dei miei progetti. Spero di essere la persona che stavi cercando per il tuo progetto.</p>
        </div>

        <div class="row">
          <div class="col-lg-12 d-flex justify-content-center" data-aos="fade-up" data-aos-delay="100">
            <ul id="portfolio-flters">
              <li data-filter="*" class="filter-active">Tutto</li>
              <li data-filter=".filter-app">Web</li>
              <li data-filter=".filter-card">Grafica</li>
              <li data-filter=".filter-web">Marketing</li>
            </ul>
          </div>
        </div>
        
        <div className="row portfolio-container" data-aos="fade-up" data-aos-delay="200">
          {portfolioImages.slice(0, 2).map((image, index) => (
            <div key={index} className="col-lg-4 col-md-6 portfolio-item filter-app" onClick={() => openLightbox(index)}>
              <div className="portfolio-wrap">
                <img src={image.src} className="img-fluid" alt={image.title} />
                <div className="portfolio-info">
                  <h4>{image.title}</h4>
                  <p>App</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Lightbox per le immagini del portfolio */}
      <Lightbox
        images={portfolioImages}
        isOpen={lightboxIsOpen}
        imageIndex={selectedImageIndex}
        onClose={() => setLightboxIsOpen(false)}
      />
    </section>
  );
};

export default Portfolio;
