import React, { useState } from 'react';
import Lightbox from 'react-image-lightbox'; // Importa il componente Lightbox da react-image-lightbox
import 'react-image-lightbox/style.css'; // Importa gli stili CSS del lightbox

const Portfolio = () => {
  const [lightboxIsOpen, setLightboxIsOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);

  const portfolioImages = [
    'assets/img/portfolio/portfolio-1.jpg',
    'assets/img/portfolio/portfolio-2.jpg',
    // Aggiungi ulteriori URL delle immagini
  ];

  const openLightbox = (imageIndex) => {
    setSelectedImageIndex(imageIndex);
    setLightboxIsOpen(true);
  };

  return (
    <section id="portfolio" className="portfolio section-bg">
      {/* Resto del tuo codice del componente Portfolio */}
      <div className="row portfolio-container" data-aos="fade-up" data-aos-delay="200">
        {portfolioImages.slice(0, 2).map((image, index) => (
          <div key={index} className="col-lg-4 col-md-6 portfolio-item filter-app">
            <div className="portfolio-wrap">
              <img src={image} className="img-fluid" alt={`App ${index + 1}`} />
              <div className="portfolio-info">
                <h4>App {index + 1}</h4>
                <p>App</p>
                <div className="portfolio-links">
                  <a onClick={() => openLightbox(index)} className="portfolio-lightbox" title={`App ${index + 1}`}><i className="bx bx-plus"></i></a>
                  {/* Rimuovi il link esterno a "portfolio-details.html" */}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {lightboxIsOpen && (
        <Lightbox
          mainSrc={portfolioImages[selectedImageIndex]}
          nextSrc={portfolioImages[(selectedImageIndex + 1) % portfolioImages.length]}
          prevSrc={portfolioImages[(selectedImageIndex + portfolioImages.length - 1) % portfolioImages.length]}
          onCloseRequest={() => setLightboxIsOpen(false)}
          onMovePrevRequest={() =>
            setSelectedImageIndex((selectedImageIndex + portfolioImages.length - 1) % portfolioImages.length)
          }
          onMoveNextRequest={() =>
            setSelectedImageIndex((selectedImageIndex + 1) % portfolioImages.length)
          }
        />
      )}
    </section>
  );
};

export default Portfolio;
